const BentoSkills: React.FC = () => {
  const { theme } = useTheme();
  const [activeTab, setActiveTab] = useState('All');
  const tabs = ['All', 'Frameworks', 'Languages', 'Databases & Cloud', 'DevOps', 'Subjects'];

  const filteredSkills = SKILLS.filter(skill => {
    if (activeTab === 'All') return true;
    if (activeTab === 'Frameworks') return skill.category === 'Web';
    if (activeTab === 'Languages') return skill.category === 'Language';
    if (activeTab === 'Databases & Cloud') return skill.category === 'DB';
    if (activeTab === 'DevOps') return skill.name === 'Docker' || skill.name === 'Git';
    if (activeTab === 'Subjects') return skill.name === 'Operating Systems' || skill.name === 'DSA';
    return true;
  });

  return (
    <section id="skills" className="py-24 px-6 relative overflow-hidden bg-theme-primary transition-colors duration-300">
      {/* Background Plexus/Nodes Effect Simulation */}
      <div className="absolute inset-0 opacity-10 pointer-events-none">
         <svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg">
            <defs>
               <pattern id="plexus-dark" width="100" height="100" patternUnits="userSpaceOnUse">
                  <circle cx="2" cy="2" r="1.5" fill="white" />
                  <path d="M2 2 L100 100 M2 2 L100 0" stroke="white" strokeWidth="0.5" opacity="0.3" />
               </pattern>
               <pattern id="plexus-light" width="100" height="100" patternUnits="userSpaceOnUse">
                  <circle cx="2" cy="2" r="1.5" fill="black" />
                  <path d="M2 2 L100 100 M2 2 L100 0" stroke="black" strokeWidth="0.5" opacity="0.3" />
               </pattern>
            </defs>
            <rect width="100%" height="100%" fill={theme === 'dark' ? 'url(#plexus-dark)' : 'url(#plexus-light)'} />
         </svg>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        <h2 className="text-5xl font-bold mb-8 text-center md:text-left text-theme-primary">Technical Expertise</h2>
        {/* Navigation Filters */}
        <div className="flex flex-wrap gap-3 mb-16 justify-center md:justify-start">
          {tabs.map(tab => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`px-6 py-2.5 rounded-full text-base font-medium transition-all duration-300 border ${
                activeTab === tab 
                ? 'bg-blue-600/90 text-white border-blue-500 shadow-lg shadow-blue-500/20' 
                : 'dark:bg-white/5 light:bg-black/5 text-theme-secondary dark:border-white/10 light:border-black/10 dark:hover:bg-white/10 light:hover:bg-black/10 hover:text-theme-primary'
              }`}
            >
              {tab}
            </button>
          ))}
        </div>

        {/* Skills Grid */}
        <motion.div 
          layout
          className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-y-12 gap-x-8"
        >
          <AnimatePresence mode="popLayout">
            {filteredSkills.map((skill) => (
              <motion.div
                key={skill.name}
                layout
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{ duration: 0.3 }}
                className="flex flex-col items-center group cursor-pointer"
              >
                <div className="relative mb-4 transform transition-transform group-hover:scale-110 duration-300">
                  <div className="absolute inset-0 bg-blue-500/0 group-hover:bg-blue-500/5 blur-2xl rounded-full transition-all duration-500" />
                  <div className="dark:brightness-125 dark:contrast-110 light:brightness-100 transition-all duration-300 relative z-10">
                    <SkillIcon name={skill.icon} />
                  </div>
                </div>
                <span className="text-theme-secondary text-base font-medium tracking-tight group-hover:text-theme-primary transition-colors duration-300">
                  {skill.name}
                </span>
              </motion.div>
            ))}
          </AnimatePresence>
        </motion.div>
      </div>
    </section>
  );
};

export default BentoSkills;
